mapper.select:
SELECT * FROM IMG_FILE
where board_id = #{boardId}
order by post_id desc

SELECT MEMBER_ID
FROM post
WHERE BOARD_ID = #{boardId}
AND POST_ID = #{postId}

SELECT * FROM IMG_FILE
WHERE
BOARD_ID = #{boardId}
AND POST_ID = #{postId}

SELECT MAX(POST_ID)
FROM POST
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE POST

SET POST_LIKE_COUNT = POST_LIKE_COUNT +1

WHERE POST_ID = #{postId}

UPDATE POST

SET POST_SCRAP_COUNT = POST_SCRAP_COUNT +1

WHERE POST_ID = #{postId}

UPDATE POST

SET POST_SCRAP_COUNT = POST_SCRAP_COUNT -1

WHERE POST_ID = #{postId}
