mapper.select:
SELECT COUNT(*) AS ScrapCount
FROM SCRAP
WHERE POST_ID = #{postId}

SELECT POST_ID
FROM SCRAP
WHERE MEMBER_ID = #{memberId}

SELECT COUNT(*)
FROM SCRAP
WHERE MEMBER_ID = #{memberId}
AND POST_ID = #{postId}

SELECT COUNT(*) AS ScrapCount
FROM SCRAP
WHERE POST_ID = #{postId} AND MEMBER_ID = #{memberId}
